package Day02;

public class FloatEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float var1 = 3.4f;  					//float 형을 사용할땐 자료형의 명시를 해줘야한다. 즉 f를 붙여줘야한다.
		double var2 = 55.55f;					//있을때와 없을때와 뒤에 나오는 수치가 다르다.
		
		System.out.println("var1의 값은 :"+var1);
		System.out.println("var2의 값은 :"+var2);
		System.out.println("var1의 double형 값은 :"+(double)var1);

	}

}
