package Day03;

// 배정연산자, 대입연산자

public class OperEx7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a= 10;
		int result = 0;
		
		result += a;							//result = result + a 
		System.out.println("+경우:"+result);
		result *= a;							//result = result * a 
		System.out.println("*경우:"+result);
		result %= a;							//result = result % a
		System.out.println("%경우:"+result);
		result /= a;							//result = result / a
		System.out.println("/경우:"+result);
		
		//=================================================================
		
		
		
		
		
		
		
		
		
		
		
		
		
	}

}
